const usedWords = [];

function getLevel(length, amount, firstDesc){
    const levelWords = [];
    const levelWordsDescriptions = {};
    const words = wordsArray2[length];
    for(let i = 0; i < words.length; i++){
        const word = words[i].toUpperCase();
        if(usedWords.includes(word)) continue;

        if(wordsDescriptions[word]){
            // Рандомное определение слова
            let desc;

            if(firstDesc){
                desc = wordsDescriptions[word][0];
            }else{
                desc = wordsDescriptions[word][Math.floor(Math.random()*wordsDescriptions[word].length)];
                while(isWordHaveStupidWords(desc) && Math.random() > 0.35){
                    desc = wordsDescriptions[word][Math.floor(Math.random()*wordsDescriptions[word].length)];
                }
            }
            if(desc === undefined) {
                console.log('bad desc');
                continue;
            }

            levelWords.push(word.replace(/Ё/g, 'Е'));
            levelWordsDescriptions[word] = desc;
            usedWords.push(word);

            if(levelWords.length === amount) {
                wordsArray2[length] = words.slice(i+1);
                return {
                    levelWords, levelWordsDescriptions
                }
            }
        }else{
            // console.log(word, ' - no ');
        }
    }


}

